import {RadioType} from "../objects/enums/RadioType";

export const getTextsByType = (radioType: RadioType) => {
    switch (radioType) {
        case RadioType.OPERATION:
            return ["XD", "XD", "XD", "XD"]
        case RadioType.TYPE:
            return ["DXD", "DXD", "DXD", "DXD"]
        case RadioType.QUANTITY:
        default:
            return ["XDD", "XDD", "XDD", "XDD"]

    }
}
